function makeDatesRelative(){var t=new Date,a=Math.floor(365/12),e=365;$("time").each(function(){var h=new Date($(this).attr("datetime").substring(0,4)+"-"+$(this).attr("datetime").substring(5,7)+"-"+$(this).attr("datetime").substring(8,10)),s=Math.floor((t-h)/1e3/60/60/24);0>s?$(this).html("The future!"):0==s?$(this).html("Today"):1==s?$(this).html("Yesterday"):s>=1&&7>s?$(this).html(s+" days ago"):s>=7&&14>s?$(this).html("last week"):s>=14&&a>s?$(this).html(Math.floor(s/7)+" weeks ago"):s>=a&&2*a>s?$(this).html("last month"):s>=2*a&&e>s?$(this).html(Math.floor(s/a)+" months ago"):s>=e&&2*e>s?$(this).html("last year"):s>=2*e&&$(this).html(Math.floor(s/e)+" years ago")})}$(document).ready(function(){makeDatesRelative()});