function makeDatesRelative(){var t=new Date,e=Math.floor(365/12),h=365;$("time").each(function(){var a=new Date($(this).attr("datetime").substring(0,4)+"-"+$(this).attr("datetime").substring(5,7)+"-"+$(this).attr("datetime").substring(8,10)),s=Math.floor((t-a)/1e3/60/60/24);0>s?$(this).html("The future!"):0==s?$(this).html("Today"):1==s?$(this).html("Yesterday"):(s>=1&&7>s?$(this).html(s+" days"):s>=7&&14>s?$(this).html("1 week"):s>=14&&e>s?$(this).html(Math.floor(s/7)+" weeks"):s>=e&&2*e>s?$(this).html("1 month"):s>=2*e&&h>s?$(this).html(Math.floor(s/e)+" months"):s>=h&&2*h>s?$(this).html("1 year"):s>=2*h&&$(this).html(Math.floor(s/h)+" years"),$(this).append(" ago"))})}$(document).ready(function(){makeDatesRelative()});